import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Employees from "./Employees"; 
import { useNavigate } from 'react-router-dom';

function Edit() {
    const [name, setName] = useState('');
    const [lname, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [id, setId] = useState('');
    const history = useNavigate();

    useEffect(() => {
        const employeeData = Employees.find(employee => employee.id === id);

        if (employeeData) {
            setName(employeeData.Name);
            setLastName(employeeData.LName);
            setEmail(employeeData.Email);
        }
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const updatedEmployees = Employees.map(employee => {
            if (employee.id === id) {
                return { ...employee, Name: name, LName: lname, Email: email };
            }
            return employee;
        });

        // Replace the existing employee data with the updated data
        // You might have a function to update your Employees state here or use another method to update the data
        // For example, if Employees is a state, you could have a function like setEmployees(updatedEmployees);

        // Redirect to the desired page after updating
        history("/");
    }

    return (
        <div>
            <Form className="d-grid gap-2" style={{ margin: "15rem" }}>
                <Form.Group className="mb-3" controlId="formName">
                    <Form.Control type="text" placeholder="Enter Name" value={name} required onChange={(e) => setName(e.target.value)} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formLastName">
                    <Form.Control type="text" placeholder="Enter Last Name" value={lname} required onChange={(e) => setLastName(e.target.value)} />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formEmail">
                    <Form.Control type="text" placeholder="Enter Email" value={email} required onChange={(e) => setEmail(e.target.value)} />
                </Form.Group>
                <Button onClick={(e) => handleSubmit(e)} type="submit">Update</Button>
            </Form>
        </div>
    )
}

export default Edit;
